/**
 * @module Either Operators
 */
import { Left } from '../left';
import { Right } from '../right';
import type { Either } from '../either';
/**
 * Type guard function to tell if the provided `monad` is `Left`.
 */
export declare function isLeft<LeftValue>(monad: Either<LeftValue, any>): monad is Left<LeftValue>;
/**
 * Type guard function to tell if the provided `monad` is `Right`.
 */
export declare function isRight<RightValue>(monad: Either<any, RightValue>): monad is Right<RightValue>;
/**
 * Type guard function to tell if the provided `value` is `Either`.
 */
export declare function isEither<LeftValue, RightValue>(value: any): value is Either<LeftValue, RightValue>;
