/**
 * @module Side Effects
 */
import type { Effect } from './types/effect';
import type { VariadicFunction } from './types/function';
/**
 * Injects a function with a side effect.
 * Returns the copy of the original function.
 */
export default function inject<Function extends VariadicFunction>(effect: Effect<Function>): (fn: Function) => Function;
/**
 * Injects a function with a side effect.
 * Returns the copy of the original function.
 */
export default function inject<Function extends VariadicFunction>(fn: Function, effect: Effect<Function>): Function;
