/**
 * @module Maybe Operators
 */
import { Just } from '../just';
import type { Maybe } from '../maybe';
import { Nothing } from '../nothing';
/**
 * A type to unwrap the value type of the provided `Monad`.
 */
export declare type Fold<Monad extends Maybe<any>> = Monad extends Just<infer Value> ? Value : Monad extends Nothing ? null : never;
/**
 * Returns the value of the provided `monad`.
 */
export default function fold<Monad extends Maybe<any>>(monad: Monad): Fold<Monad>;
