/**
 * @module Maybe Operators
 */
import { Just } from '../just';
import type { Maybe } from '../maybe';
import { Nothing } from '../nothing';
/**
 * Type guard function to tell if the provided `monad` is `Just`.
 */
export declare function isJust<Value>(monad: Maybe<Value>): monad is Just<Value>;
/**
 * Type guard function to tell if the provided `monad` is `Nothing`.
 */
export declare function isNothing<Value>(monad: Maybe<Value>): monad is Nothing;
/**
 * Type guard function to tell if the provided `value` is `Maybe`.
 */
export declare function isMaybe<Value>(value: any): value is Maybe<Value>;
